import DatePickerTemplate from '../templatemanager/templates/datePicker/datePicker';
import DateRangePickerTemplate from '../templatemanager/templates/dateRangePicker/dateRangePicker';
import ClockPickerTemplate from '../templatemanager/templates/clockPicker/clockPicker';
import RadioOptionPickerTemplate from '../templatemanager/templates/radioOptionPicker/radioOptionPicker';
import TaskPickerTemplate from '../templatemanager/templates/taskPickerTemplate/taskPickerTemplate';
/**
 * KorePicker plugin classs
 *
 * @decorator Class
 */
class KorePickersPlugin {
    name = 'KorePickersPlugin';

     /**
      * KorePicker configuration
      */
    config = {
        /**
         * To set the clock widget configuration
         */
        clock:{
            /**
             * To append clock picker icon to the footer
             */
            showOnFooter:false,
            text: "display",
            /**
             * Title to display
             */
            title: "Please choose",
        },
        /**
         * To set the daterange widget configurations
         */
        daterange:{
            /**
             * To append datepicker icon to the footer
             */
            showOnFooter:false,
            text: "display",
            /**
             * Title to display
             */
            title: "Please choose",
            /**
             *  TO set the format  of date
             */
            format: 'DD-MM-YYYY',
            /**
             *  Specifies start date for range selection
             */
            startDate: '',
            /**
             * Specifies end date for range selection
             */
            endDate: ''
        },
        /**
         * To set the datepicker widget configuration
         */
        date:{
            /**
             * To append datepicker icon to the footer
             */
            showOnFooter:false,

            text: "display",
            /**
             *  Title to display
             */
            title: "Please choose",
            /**
             * Date format
             */
            format: 'DD-MM-YYYY',
            /**
             * Specifies start date for selection
             */
            startDate: '',
            /**
             * Specifies end date for selection
             */
            endDate: ''
        },
        /**
         * To show the task menu
         */
        task:{
            /**
             *  To append task icon to the footer
             */
            showOnFooter:false,
            "tasks": [
                {
                    /**
                     * Title of the task
                     */
                    "title": "Get balance",
                    /**
                     * Icon for the task
                     */
                    "icon": 'data:image/png;base64,',
                    /**
                     * To send the payload to the bot
                     */
                    "postback": {
                        /**
                         *  Message to be displayed after selecting the task
                         */
                        "title": "Get my leave balance",
                        /**
                         * Value sent to bot as payload
                         */
                        "value": "leaveintent"
                    }
                },
                {
                    "title": "Change Password",
                    "icon": 'data:image/png;base64,',
                    "postback": {
                        "title": "change my password",
                        "value": "leaveintent"
                    }
                },
                {
                    "title": "Pay Bill",
                    "icon": 'data:image/png;base64,',
                    "postback": {
                        "title": "Pay the bill",
                        "value": "leaveintent"
                    }
                },
                {
                    "title": "Setup Balance Alert",
                    "icon": 'data:image/png;base64,',
                    "postback": {
                        "title": "Give alert message for balance",
                        "value": "leaveintent"
                    }
                }
            ],
            /**
             * Heading for the task picker
             */
            "heading": "Choose Task"
        },
        /**
         * Show the radio options
         */
        radiooption:{
            /**
             * To append radio option picker icon to the footer
             */
            showOnFooter:false,
            /**
             * Heading to be displayed
             */
            "heading": "Please choose",
            "radioOptions": [
                {
                    /**
                     *  Title for the task
                     */
                    "title": "Shanmuga",
                    /**
                     * Description for the task
                     */
                    "value": "1234 4567 5678 6789",
                    /**
                     * Send payload options to the bot
                     */
                    "postback": {
                        /**
                         * Message to be displayed after selection of radio option
                         */
                        "title": "Transaction Successful",
                        /**
                         * Value that is sent to bot as payload
                         */
                        "value": "Payment Successful"
                    }
                },
                {
                    "title": "Madhu",
                    "value": "1234 4567 5678 9876",
                    "postback": {
                        "title": "radioOptionDetails",
                        "value": "AccountData"
                    }
                },
                {
                    "title": "Santhosh",
                    "value": "4678 1234 5678 9876",
                    "postback": {
                        "title": "Get my leave balance",
                        "value": "leaveintent"
                    }
                },
                {
                    "title": "Manjula",
                    "value": "9876 1234 4567 5678",
                    "postback": {
                        "title": "Transaction Successful",
                        "value": "leaveintent"
                    }
                },
                {
                    "title": "Ravi Kiran",
                    "value": "8976 5677 7946 2345",
                    "postback": {
                        "title": "radioOptionDetails",
                        "value": "leaveintent"
                    }
                },
            ]
        }

    };
    pickerHTML:any;
    hostInstance: any;
    constructor(pickerConfig:any) {
        let me = this;
        me.extend(this.config,pickerConfig);
    }
    onHostCreate() {
        let me = this;
        let cwInstance=me.hostInstance;
        cwInstance.on("viewInit", (chatWindowEle:any) => {
            me.onInit();
        });
       
    }
    onInit() {
        let me = this;
        me.installPickerTemplates();
        me.appendPickersToChatWindow();
    }
    installPickerTemplates(){
        let me=this;
        let templateManager = me.hostInstance.templateManager;
        templateManager.installTemplate(new DatePickerTemplate());
		templateManager.installTemplate(new DateRangePickerTemplate());
		templateManager.installTemplate(new ClockPickerTemplate());
		templateManager.installTemplate(new RadioOptionPickerTemplate());
		templateManager.installTemplate(new TaskPickerTemplate());
    }
    appendPickersToChatWindow() {
        let me = this;
        let $ = me.hostInstance.$;
        let chatWindowInstance = me.hostInstance;
        if (this.config && this.config?.clock?.showOnFooter) {
            me.pickerHTML = $(me.getClockPickerTemplateString());
            me.appendPickerHTMLtoChatWindowFooter(me.pickerHTML);
            me.bindEvents(me.pickerHTML);
        }
        if (this.config && this.config?.date?.showOnFooter) {
            me.pickerHTML = $(me.getDatePickerTemplateString());
            me.appendPickerHTMLtoChatWindowFooter(me.pickerHTML);
            me.bindEvents(me.pickerHTML);
        }
        if (this.config && this.config?.daterange?.showOnFooter) {
            me.pickerHTML = $(me.getDateRangePickerTemplateString());
            me.appendPickerHTMLtoChatWindowFooter(me.pickerHTML);
            me.bindEvents(me.pickerHTML);
        }
        if (this.config && this.config?.task?.showOnFooter) {
            me.pickerHTML = $(me.getTaskPickerTemplateString());
            me.appendPickerHTMLtoChatWindowFooter(me.pickerHTML);
            me.bindEvents(me.pickerHTML);
        }
        if (this.config && this.config?.radiooption.showOnFooter) {
            me.pickerHTML = $(me.getRadioOptionPickerTemplateString());
            me.appendPickerHTMLtoChatWindowFooter(me.pickerHTML);
            me.bindEvents(me.pickerHTML);
        }
    }
    appendPickerHTMLtoChatWindowFooter(pickerHTML:any){
        let me = this;
        let chatWindowInstance = me.hostInstance;
        const _chatContainer = chatWindowInstance.chatEle;
        _chatContainer.find('.kore-chat-footer .footerContainer').append(pickerHTML);        
    }
    getClockPickerTemplateString() {
        var clockFooterTemplate = '<div class="sdkFooterIcon clock"> \
            <button type="button"  class="sdkClock clockBtn" title="Clock"> \
                <i class="clock"></i> \
            </button> \
        </div>';
        return clockFooterTemplate;
    }
    getDatePickerTemplateString = function () {
        var dateFooterTemplate = '<div class="sdkFooterIcon singleCalender"> \
                                    <button name="dates" class="sdkCalender calenderBtn" title="Calender"> \
                                        <i class="calender"></i> \
                                    </button> \
                                 </div>';
        return dateFooterTemplate;
    }
    getDateRangePickerTemplateString = function () {
        var dateRangeFooterTemplate = '<div class="sdkFooterIcon rangeCalender"> \
                                            <button name="dates" class="sdkRangeCalender calenderBtn" title="RangeCalender"> \
                                                <i class="Rangecalender"></i> \
                                            </button> \
                                        </div>';
        return dateRangeFooterTemplate;
    }
    getTaskPickerTemplateString = function () {
        var TaskFooterTemplate = '<div class="sdkFooterIcon menu"> \
                                    <button type="button"  class="sdkMenu menuBtn" title="TaskMenu"> \
                                        <i class="menuIcon"></i> \
                                    </button> \
                                 </div>';
        return TaskFooterTemplate;
    }
    getRadioOptionPickerTemplateString = function () {
        var AccountFooterTemplate = '<div class="sdkFooterIcon account"> \
                                        <button type="button"  class="sdkAccount accountBtn" title="AccountMenu"> \
                                            <i class="accountIcon"></i> \
                                        </button> \
                                    </div>';
        return AccountFooterTemplate;
    }
    bindEvents(element:any) {
        let me = this;
        let $ = me.hostInstance.$;
        let chatWindowInstance = me.hostInstance;
        $(element).on('click', '.sdkClock.clockBtn',  (event:any)=> {
            this.showClockPicker()
        });
        $(element).on('click', '.sdkCalender.calenderBtn',  (event: any)=> {
            this.showDatePicker()
        });
        $(element).on('click', '.sdkRangeCalender.calenderBtn',  (event: any)=> {
            this.showDateRangePicker();
        });
        $(element).on('click', '.sdkAccount.accountBtn',  (event: any) => {
            this.showRadioOptionPicker();
        });
        $(element).on('click', '.sdkMenu.menuBtn', (event: any) => {
            this.showTaskPicker();
        });
    }

    showClockPicker = (options?:any) =>  {
        let me = this;
        if(options){
            me.config.clock = {
                ...me.config.clock,
                ...options
            }
        }
        let $ = me.hostInstance.$;
        let chatWindowInstance = me.hostInstance;
        var tempMessageData = {
            message: [
                {
                    component: {
                        payload: {
                            template_type: "clockTemplate",
                            text: me.config.clock.text,
                            title: me.config.clock.title,
                        },
                        type: "template"
                    },
                }
            ]
        }
        chatWindowInstance.renderMessage(tempMessageData);
    }
    showDatePicker=(options?:any)=> {
        let me = this;
        if(options){
            me.config.date = {
                ...me.config.date,
                ...options
            }
        }
        let $ = me.hostInstance.$;
        let chatWindowInstance = me.hostInstance;
        var tempMessageData = {
            message: [
                {
                    component: {
                        payload: {
                            template_type: "dateTemplate",
                            text: me.config.date.text,
                            title: me.config.date.title,
                            format: me.config.date.format,
                            startDate: me.config.date.startDate,
                            endDate: me.config.date.endDate
                        },
                        type: "template"
                    },
                }
            ]
        }
        chatWindowInstance.renderMessage(tempMessageData);
    }

    showDateRangePicker=(options?:any)=>  {
        let me = this;
        if(options){
            me.config.daterange = {
                ...me.config.daterange,
                ...options
            }
        }
        let $ = me.hostInstance.$;
        let chatWindowInstance = me.hostInstance;
        var tempMessageData = {
            message: [
                {
                    component: {
                        payload: {
                            template_type: "daterange",
                            text: me.config.daterange.text,
                            title: me.config.daterange.title,
                        },
                        type: "template"
                    },
                }
            ]
        }
        chatWindowInstance.renderMessage(tempMessageData);
    }

    showRadioOptionPicker=(options?:any)=>  {
        let me = this;
        if(options){
            me.config.radiooption.radioOptions = options;
        }
        let $ = me.hostInstance.$;
        let chatWindowInstance = me.hostInstance;
        var tempMessageData = {
            message: [
                {
                    component: {
                        "payload": {
                            "template_type": "radioOptionTemplate",
                            "heading": me.config.radiooption.heading,
                            "radioOptions": me.config.radiooption.radioOptions,
                        }
                    },
                }
            ]
        }
        chatWindowInstance.renderMessage(tempMessageData);// radio options
    }

    showTaskPicker=(options?:any)=> {
        let me = this;
        if(options){
            me.config.task.tasks = options;
        }
        let $ = me.hostInstance.$;
        let chatWindowInstance = me.hostInstance;
        var tempMessageData = {
            message: [
                {
                    component: {
                        "payload": {
                            "template_type": "taskPickerTemplate",
                            "tasks": me.config.task.tasks,
                            "heading": me.config.task.heading,
                        },
                    },
                }
            ]
        }
        chatWindowInstance.renderMessage(tempMessageData);
    }
    extend(target:any, source:any) {
        let me:any=this;
        for (var prop in source) {
            if (source.hasOwnProperty(prop)) {
                if (target[prop] && typeof source[prop] === 'object') {
                    me.extend(target[prop], source[prop]);
                }
                else {
                    target[prop] = source[prop];
                }
            }
        }
        return target;
      }

}
export default KorePickersPlugin;
